mapper.select:
select 

from student_guardion
where schoolid = #{schoolid,jdbcType=INTEGER}
  and guardionid = #{guardionid,jdbcType=INTEGER}
  and studentid = #{studentid,jdbcType=INTEGER}

mapper.delete:
delete from student_guardion
where schoolid = #{schoolid,jdbcType=INTEGER}
  and guardionid = #{guardionid,jdbcType=INTEGER}
  and studentid = #{studentid,jdbcType=INTEGER}

mapper.update:
update student_guardion
set relation = #{relation,jdbcType=VARCHAR},
  important = #{important,jdbcType=INTEGER},
  updatetime = #{updatetime,jdbcType=TIMESTAMP},
  createuid = #{createuid,jdbcType=INTEGER},
  createtime = #{createtime,jdbcType=TIMESTAMP},
  updateuid = #{updateuid,jdbcType=INTEGER},
  del = #{del,jdbcType=BIT}
where schoolid = #{schoolid,jdbcType=INTEGER}
  and guardionid = #{guardionid,jdbcType=INTEGER}
  and studentid = #{studentid,jdbcType=INTEGER}
