mapper.select:
select 

from school_attendance_record
where id = #{id,jdbcType=INTEGER}
  and schoolid = #{schoolid,jdbcType=INTEGER}

mapper.delete:
delete from school_attendance_record
where id = #{id,jdbcType=INTEGER}
  and schoolid = #{schoolid,jdbcType=INTEGER}

mapper.insert:
insert into school_attendance_record (id, schoolid, type, 
  userid, verifycode, starttime, 
  endtime, sensorid, sn, 
  pictureurl, status, del
  )
values (#{id,jdbcType=INTEGER}, #{schoolid,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, 
  #{userid,jdbcType=INTEGER}, #{verifycode,jdbcType=VARCHAR}, #{starttime,jdbcType=TIMESTAMP}, 
  #{endtime,jdbcType=TIMESTAMP}, #{sensorid,jdbcType=VARCHAR}, #{sn,jdbcType=VARCHAR}, 
  #{pictureurl,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{del,jdbcType=BIT}
  )
